import s from './s.module.scss';
import { CaretDownOutlined,CaretUpOutlined } from '@ant-design/icons';
import React, { useRef } from "react";
import { useDetectOutsideClick } from "./useDetectOutsideClick";


export const Menu = () => {
    const dropdownRef = useRef(null);
    const [isActive, setIsActive] = useDetectOutsideClick(dropdownRef, false);
    const onClick = () => setIsActive(!isActive);
    return (
            <div className={s.price}>
                
                
                    <button className={s.firtsBt} onClick={onClick}> Мои билеты </button>
                    <button onClick={onClick}> Подписки </button>
                    <button onClick={onClick}> Избранное </button>
                    <button onClick={onClick}> Выход </button>
                    <button onClick={onClick}> Настройки </button>
                    <button className={s.lastBt} onClick={onClick}> Стать организатором </button>
                
            </div>
    );
  };
